import string
from chatterbot import languages
import spacy


class PosLemmaTagger(object):
    def __init__(self, language="en_core_web_sm"):
        self.language = language
        self.nlp = spacy.load(self.language)
        
        # Create a translation table to remove punctuation
        self.punctuation_table = str.maketrans('', '', string.punctuation)

    def get_bigram_pair_string(self, text):
        # Ensure that the text has punctuation removed using the punctuation_table
        text_without_punctuation = text.translate(self.punctuation_table)
        # Further processing can go here
        # For example, creating bigrams or processing with spaCy
        return text_without_punctuation

    def get_bigram_pair_string(self, text):
        """
        Return a string of text containing part-of-speech, lemma pairs.
        """
        bigram_pairs = []

        if len(text) <= 2:
            text_without_punctuation = text.translate(self.punctuation_table)
            if len(text_without_punctuation) >= 1:
                text = text_without_punctuation

        document = self.nlp(text)

        if len(text) <= 2:
            bigram_pairs = [
                token.lemma_.lower() for token in document
            ]
        else:
            tokens = [
                token for token in document if token.is_alpha and not token.is_stop
            ]

            if len(tokens) < 2:
                tokens = [
                    token for token in document if token.is_alpha
                ]

            for index in range(1, len(tokens)):
                bigram_pairs.append('{}:{}'.format(
                    tokens[index - 1].pos_,
                    tokens[index].lemma_.lower()
                ))

        if not bigram_pairs:
            bigram_pairs = [
                token.lemma_.lower() for token in document
            ]

        return ' '.join(bigram_pairs)
